FOAModel({ name: 'Test' });
var x = Test.create({});

var barCount = 0;
var bazCount = 0;
x.subscribe(['foo', 'bar'], function() { barCount++; });
x.subscribe(['foo', 'baz'], function() { bazCount++; });

assert(barCount === 0 && bazCount === 0, 'Both counts start at 0');

x.publish(['foo', 'bar']);
assert(barCount === 1 && bazCount === 0, 'Bar was incremented');

x.publish(['foo', 'baz']);
assert(barCount === 1 && bazCount === 1, 'Baz was incremented');

x.publish(['foo']);
assert(barCount === 1 && bazCount === 1, 'Publishing on a prefix does nothing');

x.publish(['foo', EventService.WILDCARD]);
assert(barCount === 2 && bazCount === 2, 'But publishing on WILDCARD hits all events in the subtree');

