FOAModel({
  name: 'OMTestBase',
  properties: [
    { name: 'foo', defaultValue: 'rabbits' },
    'bar'
  ],
  methods: {
    doThings: function() {
      seq.push('doThings base');
    }
  }
});

seqMatches = function(gold) {
  if ( seq.length !== gold.length ) return false;
  for ( var i = 0; i < seq.length ; i++ ) {
    if ( seq[i] != gold[i] ) return false;
  }
  return true;
}

var x = OMTestBase.create({ bar: 'baz' });
assert(x.bar === 'baz', 'Constructors can set properties');
assert(x.foo === 'rabbits', 'Default values are used if not given');

x.foo = 'kittens';
assert(x.foo === 'kittens', 'But the defaults can be overridden');

var y = OMTestBase.create(x);
assert(y.foo == x.foo, 'FOAM objects can be passed to constructors to copy');
y.foo = 'puppies';
assert(y.foo === 'puppies' && x.foo === 'kittens', 'And it\'s really a copy');

var model = Model.create({
  name: 'OMTestDefaults',
  properties: [
    {
      name: 'foo',
      defaultValueFn: function(){ seq.push('foo dVF'); return 7; }
    },
    {
      name: 'bar',
      factory: function() { seq.push('bar factory'); return 9; }
    }
  ]
});

var x = model.create({});
assert(seqMatches(['bar factory']), 'Factories are called once on init, but defaultValueFns are not');

assert(x.foo === 7, 'DefaultValueFns work');
assert(seqMatches(['bar factory', 'foo dVF']), 'Requesting x.foo calls the foo property\'s defaultValueFn');
assert(x.foo === 7, 'Still just the defaultValueFn');
assert(seqMatches(['bar factory', 'foo dVF', 'foo dVF']),
    'Requesting x.foo again calls its defaultValueFn again');
assert(x.bar === 9, 'The factory property was correctly set too');
assert(seqMatches(['bar factory', 'foo dVF', 'foo dVF']),
    'Requesting x.bar does not call the factory again');

x.foo = 17;
assert(seqMatches(['bar factory', 'foo dVF', 'foo dVF', 'foo dVF']),
    'Overwriting the value causes one more defaultValueFn call');

assert(x.foo === 17, 'Overwriting the property with the defaultValueFn works');
assert(seqMatches(['bar factory', 'foo dVF', 'foo dVF', 'foo dVF']),
    'And now requesting it does not call the defaultValueFn');
x.foo = 7;
assert(x.foo === 7, 'Setting the property to the default value still doesn\'t return to calling the defaultValueFn');
assert(seqMatches(['bar factory', 'foo dVF', 'foo dVF', 'foo dVF']),
    'Requesting it still doesn\'t call defaultValueFn');

var sub = Model.create({
  name: 'OMTestSubclass',
  extendsModel: 'OMTestBase',
  properties: [
    { name: 'foo', defaultValue: 'kittens' },
    'baz'
  ],
  methods: {
    doThings: function() {
      seq.push('subclass pre');
      this.SUPER();
      seq.push('subclass post');
    }
  }
});

var x = sub.create({});
assert(x.foo === 'kittens', 'Default values of base class properties can be overridden');
assert(x.bar$, 'Properties not mentioned in the subclass still exist');
assert(x.baz$, 'Properties added by the subclass exist too');

seq = [];
assert(seqMatches([]), 'seq starts empty');

x.doThings();
assert(seqMatches(['subclass pre', 'doThings base', 'subclass post']),
    'this.SUPER() calls the same method on the superclass, allowing you to add code before and after it');

